package boletin04;

public class Prueba {

	public static void main(String[] args) throws InterruptedException {
		// TODO Auto-generated method stub
		String s = "    hola  ";  
	    System.out.println(s.trim());   //metodo trim() que quita los espacion del los extremos de un string
	    System.out.println("\n");
	    Thread.sleep(2000);     //metodo sleep() que hace una pausa donde puede asignar segundos
	    System.out.println("\n");
	    System.out.println(s.toUpperCase()); //metodo que toUpperCase() que convierte las letras en mayuscula
	    

	}

}
